mapper.select:
select
,
from tb_zhonghe_mall_activity where activity_name = #{activityName,jdbcType=VARCHAR} and organization_id = #{organizationId,jdbcType=BIGINT}

mapper.update:
update tb_zhonghe_mall_activity
set status=#{status},
update_time=now() ,
update_user=#{adminId}
where activity_id in

    #{id}

mapper.select:
select

from tb_zhonghe_mall_activity


        and activity_name like CONCAT('%',#{activityName},'%')


        and organization_id = #{organizationId}


        and status = #{status}


        and DATE_FORMAT(endtime,'%Y-%m-%d %H:%i:%s') >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')


        and DATE_FORMAT(starttime,'%Y-%m-%d %H:%i:%s') <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')


order by activity_id desc

    limit #{start},#{limit}

select

from tb_zhonghe_mall_activity


        and (activity_name like CONCAT('%',#{keyword},'%') or activity_intro like CONCAT('%',#{keyword},'%'))


        and organization_id = #{organizationId}


        and status = #{status}






            order by activity_id desc







            order by starttime desc




    limit #{start},#{limit}

select count(*) from tb_zhonghe_mall_activity


        and (activity_name like CONCAT('%',#{keyword},'%') or activity_intro like CONCAT('%',#{keyword},'%'))


        and organization_id = #{organizationId}


        and status = #{status}

select

from tb_zhonghe_mall_activity
where activity_id in

    #{id}

order by field(activity_id,

    #{id}

);

select count(*) from tb_zhonghe_mall_activity


        and activity_name like CONCAT('%',#{activityName},'%')


        and status = #{status}

select count(*) from tb_zhonghe_mall_activity


        and activity_name like CONCAT('%',#{activityName},'%')


        and organization_id = #{organizationId}


        and status = #{status}

mapper.delete:
update from tb_zhonghe_mall_activity
